import { useEffect } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";

import { updateQuotes } from "../data/actions";

import { fetchQuotes } from "../data/apiMediator";

import { ListItem, WalletOverview } from "../components";

import { CURRENCY_TYPES } from "../data/enums";

const mapStateToProps = (state, ownProps) => {
  return { quotes: state.quotes };
};

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({ updateQuotes }, dispatch);
};

const Wallets = (props) => {
  const { updateQuotes, quotes } = props;

  useEffect(() => {
    const getQuotes = setInterval(async () => {
      const quotes = await fetchQuotes();
      updateQuotes(quotes);
    }, 1000);
    return () => clearInterval(getQuotes);
  }, []);

  return (
    <div>
      <div>{quotes.BTC_CAD}</div>
      <ul>
        <ListItem>
          <WalletOverview type={CURRENCY_TYPES.CAD} balance />
        </ListItem>
        <ListItem>
          <WalletOverview />
        </ListItem>
        <ListItem>
          <WalletOverview />
        </ListItem>
      </ul>
    </div>
  );
};

export default connect(mapStateToProps, mapDispatchToProps)(Wallets);
